import React, { useEffect, useState } from 'react';

const PairStudents = () => {
  const [students, setStudents] = useState([]);
  const [isPairingDone, setIsPairingDone] = useState(false);

  useEffect(() => {
    // Fetch the student data from the database
    fetchStudents();
  }, []);

  const fetchStudents = () => {
    // Make an API call to fetch the student data from the database
    // Replace this with your own API endpoint
    fetch('http://localhost:5000/api/users/')
      .then((response) => response.json())
      .then((data) => {
        // Filter the students based on role
        const filteredStudents = data.filter(
          (student) => student.role === 'Student'
        );
        setStudents(filteredStudents);
      })
      .catch((error) => {
        console.error('Error fetching students:', error);
      });
  };

  const pairStudents = (students, teamSize) => {
    const shuffledStudents = [...students].sort(() => Math.random() - 0.5);
    const pairedStudents = [];
    let currentIndex = 0;

    while (currentIndex < shuffledStudents.length) {
      pairedStudents.push(
        shuffledStudents.slice(currentIndex, currentIndex + teamSize)
      );
      currentIndex += teamSize;
    }

    return pairedStudents;
  };

  const handlePairing = () => {
    setIsPairingDone(true);
  };

  const morningSessionPairs = isPairingDone ? pairStudents(students, 3) : [];
  const pairProgrammingPairs = isPairingDone ? pairStudents(students, 2) : [];
  const standUpPairs = isPairingDone ? pairStudents(students, 4) : [];
  return (
    <div>
      <h3 className="text-center">Pair Students</h3>
      <button onClick={handlePairing}>Pair Students</button>

      <h2>Morning Session Pairs:</h2>
      <ul>
        {morningSessionPairs.map((pair, index) => (
          <li key={index}>{pair.join(', ')}</li>
        ))}
      </ul>

      <h2>Pair Programming Pairs:</h2>
      <ul>
        {pairProgrammingPairs.map((pair, index) => (
          <li key={index}>{pair.join(', ')}</li>
        ))}
      </ul>

      <h2>StandUp Pairs:</h2>
      <ul>
        {standUpPairs.map((pair, index) => (
          <li key={index}>{pair.join(', ')}</li>
        ))}
      </ul>
    </div>
  );
};

export default PairStudents;
