import React, { useState } from 'react';
import { works } from '../data';

const Test = () => {
  const [activeModuleIndex, setActiveModuleIndex] = useState(0);
  const [activeTaskIndex, setActiveTaskIndex] = useState(null);
  const [work, setWorks] = useState(works);

  const handleModuleClick = (index) => {
    setActiveModuleIndex(index);
  };

  const handlePopupOpen = (index) => {
    setActiveTaskIndex(index);
  };

  const handlePopupClose = () => {
    setActiveTaskIndex(null);
  };

  const handleTaskSubmit = (index) => {
    const newWorks = [...works];
    const task = newWorks[activeModuleIndex].blocks.flatMap((block) =>
      block.days.flatMap((day) => day.tasks)
    )[index];
    task.status = 'completed';
    setActiveTaskIndex(null);
    setWorks(newWorks);
  };

  return (
    <div>
      {works[activeModuleIndex].blocks.map((block, blockIndex) => (
        <div key={blockIndex} className="accordion">
          <div className="accordion-item">
            <h2 className="accordion-header">
              <button
                className="accordion-button"
                type="button"
                data-bs-toggle="collapse"
                data-bs-target={`#block-${blockIndex}`}
              >
                {block.blockName}
              </button>
            </h2>
            <div
              id={`block-${blockIndex}`}
              className="accordion-collapse collapse show"
              data-bs-parent=".accordion"
            >
              <div className="accordion-body">
                {block.days.map((day, dayIndex) => (
                  <div key={dayIndex} className="day">
                    <h3 className="daytime">{day.date}</h3>
                    <table className="table">
                      <thead>
                        <tr>
                          <th>Task Name</th>
                          <th>Type</th>
                          <th>Time</th>
                          <th>Status</th>
                          <th>Action</th>
                        </tr>
                      </thead>
                      <tbody>
                        {day.tasks.map((task, taskIndex) => (
                          <tr key={taskIndex}>
                            <td>
                              <a href={task.taskLink}>{task.taskName}</a>
                            </td>
                            <td>{task.type}</td>
                            <td>{task.time}</td>
                            <td>
                              <span
                                id={task.taskIndex}
                                className={`status ${
                                  task.type === 'Project' ? 'special-class' : ''
                                }`}
                              >
                                {task.status}
                              </span>
                            </td>
                            <td>
                              <div className="btn-group">
                                <button
                                  className="btn btn-secondary btn-sm dropdown-toggle"
                                  type="button"
                                  data-bs-toggle="dropdown"
                                  aria-expanded="false"
                                >
                                  Actions
                                </button>
                                <ul className="dropdown-menu">
                                  <li>
                                    <a
                                      href={task.taskLink}
                                      className="dropDown-list"
                                    >
                                      View Activity
                                    </a>
                                  </li>
                                  <li>
                                    <button
                                      className="dropDown-list"
                                      onClick={() =>
                                        handlePopupOpen(task.taskIndex)
                                      }
                                    >
                                      Popup
                                    </button>
                                  </li>
                                  <li>
                                    <button
                                      onClick={() =>
                                        handleTaskSubmit(taskIndex)
                                      }
                                    >
                                      Submit
                                    </button>
                                  </li>
                                </ul>
                              </div>
                              {activeTaskIndex === taskIndex && (
                                <div className="popup">
                                  <div className="popup-content">
                                    <button
                                      className="close"
                                      onClick={handlePopupClose}
                                    >
                                      X
                                    </button>
                                    <p>
                                      This is the popup for task {taskIndex + 1}
                                      .
                                    </p>
                                  </div>
                                </div>
                              )}
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Test;
