/* eslint-disable array-callback-return */
import React, { useEffect, useState } from 'react';
import { Button, Modal } from 'react-bootstrap';
import { works } from './Progress_section/data';
import DashboardSection from './dashboard_section';
import Attendance from './attendance';
import HomeProgress from './Progress_section/home';

const Student = () => {
  //active module
  const [activeModuleIndex, setActiveModuleIndex] = useState(0);
  //review

  //accessing tasks in a useState

  //Routing links
  const [selectedSection, setSelectedSection] = useState(
    JSON.parse(localStorage.getItem('selectedSection')) || 'dashboard-section'
  );

  //save to localStorage
  // links
  useEffect(() => {
    localStorage.setItem('selectedSection', JSON.stringify(selectedSection));
  }, [selectedSection]);
  //state of task status
  useEffect(() => {
    localStorage.setItem('status', JSON.stringify(status));
  }, [status]);

  const submit = (index) => {
    setStatus((prevStatus) => {
      const newStatus = [...prevStatus];
      newStatus[index] = 'Completed';
      return newStatus;
    });
    setclicked(true);
    console.log(index);
    console.log(status);
  };

  const submitRe = (index) => {
    setStatusRe((prevStatus) => {
      const newStatus = [...prevStatus];
      newStatus[index] = 'Pending';
      return newStatus;
    });
    setclicked(true);
    console.log(index);
    console.log(statusRe);
  };

  //active module function
  const handleModuleClick = (index) => {
    setActiveModuleIndex(index);
  };

  // Define a function to handle clicks on navigation links
  function handleNavigationClick(section) {
    setSelectedSection(section);
  }

  const handleRequestReview = () => {
    if (reviewsRequested < 5) {
      setShowReviewForm(true);
      setReviewsRequested(reviewsRequested + 1);
    } else {
      // display a message or disable the button
      console.log('error');
    }
  };
  //getting input request review form
  const [projectLink, setProjectLink] = useState('');
  const [confirmOriginalWork, setConfirmOriginalWork] = useState(false);
  const [isLinkValid, setIsLinkValid] = useState(false);
  const [taskLink, setTasLink] = useState('');
  //link valindation
  const validateLinkFormat = (link) => {
    const linkRegex =
      /^https:\/\/github\.com\/[a-zA-Z0-9_-]+\/[a-zA-Z0-9_-]+\/pull\/[0-9]+$/;
    return linkRegex.test(link);
  };
  const handleLinkChange = (e) => {
    setProjectLink(e.target.value);
    setIsLinkValid(validateLinkFormat(e.target.value));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Task:', taskLink);
    console.log('Project Link:', projectLink);
    console.log('Confirm Original Work:', confirmOriginalWork);
  };
  return (
    <div className="main-container">
      <aside>
        <section className="home">
          <h5>Home</h5>
          <ul className="list">
            <a
              href="/"
              onClick={() => handleNavigationClick('dashboard-section')}
            >
              <li>
                <i className="fa fa-home" aria-hidden="true"></i>
              </li>
              <li>Dashboard</li>
            </a>
          </ul>
        </section>
        <section className="lern">
          <h5>Learn</h5>
          <ul className="list">
            <a
              href="/progress-section-link"
              onClick={() => handleNavigationClick('progress-section')}
            >
              <li>
                <i className="fa fa-tasks" aria-hidden="true"></i>
              </li>
              <li>Progress</li>
            </a>
          </ul>
          <ul className="list">
            <a
              href="/attendance-section-link"
              onClick={() => handleNavigationClick('attendance-section')}
            >
              <li>
                <i className="fa fa-calendar" aria-hidden="true"></i>
              </li>
              <li>Attendance</li>
            </a>
          </ul>
          <ul className="list">
            <a
              href="/attendance-section-link"
              onClick={() => handleNavigationClick('attendance-section')}
            >
              <li>
                <i className="fa fa-calendar" aria-hidden="true"></i>
              </li>
              <li>Refer a friend</li>
            </a>
          </ul>
        </section>
      </aside>
      <main>
        <header>
          <ul className="nav">
            <li>Logo</li>
            <li>
              <a href="/codereviewer">Code Reviewer</a>
            </li>
            <li>
              <a href="#signout">Signout</a>
            </li>
          </ul>
        </header>

        <section className="all">
          {/*dashboard-section*/}
          {selectedSection === 'dashboard-section' && (
            <>
              <DashboardSection />
            </>
          )}

          {/*Progress section */}
          {selectedSection === 'progress-section' && <HomeProgress />}
          {/*Attendance section */}
          {selectedSection === 'attendance-section' && <Attendance />}
        </section>
      </main>
    </div>
  );
};

export default Student;
