import { useContext } from "react";
import {ReviewContext} from '../Helper/Context'
import {REQUEST_REVIEW} from '../Helper/action'

const StudentDashboard = () => {
    const { state, dispatch } = useContext(ReviewContext);
  
    const handleRequestReview = () => {
      const link = prompt('Enter the link to the pull request:');
      if (link) {
        dispatch({
          type: REQUEST_REVIEW,
          payload: {
            link
          }
        });
      }
    };
  
    return (
      <div>
        <h1>Student Dashboard</h1>
        {state.numRequests > 5 ? (
          <button onClick={handleRequestReview}>Request Review</button>
        ) : (
          <p>You have reached the limit of 5 review requests.</p>
        )}
        <p>Review Requests:</p>
        <ul>
          {state.studentRequests.map((request, index) => (
            <li key={index}>
              <a href={request.link} target="_blank" rel="noopener noreferrer">{request.link}</a> - {request.status}
            </li>
          ))}
        </ul>
      </div>
    );
  };
export default StudentDashboard;
