import {REQUEST_REVIEW, APPROVE_REVIEW, REJECT_REVIEW, } from './action'
export const reviewReducer = (state, action) => {
    switch (action.type) {
      case REQUEST_REVIEW:
        if (state.studentRequests.length < 5 && state.numRequests < 5) {
          const newRequest = {
            link: action.payload.link,
            status: 'Pending'
          };
          return {
            ...state,
            studentRequests: [...state.studentRequests, newRequest],
            codeReviewerRequests: [...state.codeReviewerRequests, newRequest],
            numRequests: state.numRequests + 1
          };
        } else {
          return state;
        }
      case APPROVE_REVIEW:
        const updatedApproveRequests = state.studentRequests.map(request => {
          if (request.link === action.payload.link) {
            return {
              ...request,
              status: 'Approved'
            };
          } else {
            return request;
          }
        });
        return {
          ...state,
          studentRequests: updatedApproveRequests
        };
      case REJECT_REVIEW:
        const updatedRejectRequests = state.studentRequests.map(request => {
          if (request.link === action.payload.link) {
            return {
              ...request,
              status: 'Required Changes'
            };
          } else {
            return request;
          }
        });
        return {
          ...state,
          studentRequests: updatedRejectRequests
        };
      default:
        return state;
    }
  };
