import { createContext, useContext, useEffect, useReducer } from 'react';
import './App.css';

const StudentContext = createContext();

const initialState = {
  studentRequests: JSON.parse(localStorage.getItem('studentRequests')) || [],
  reviewCount: parseInt(localStorage.getItem('reviewCount'), 10) || 5,
  click: false,
  status: 'Request a review',
};

function reducer(state, action) {
  switch (action.type) {
    case 'SET_STUDENT_REQUESTS':
      return { ...state, studentRequests: action.payload };
    case 'SET_REVIEW_COUNT':
      return { ...state, reviewCount: action.payload };
    case 'SET_CLICK':
      return { ...state, click: action.payload };
    case 'SET_STATUS':
      return { ...state, status: action.payload };
    case 'DECREMENT_REVIEW_COUNT':
      return { ...state, reviewCount: state.reviewCount - 1 };
    default:
      return state;
  }
}

function App() {
  const [state, dispatch] = useReducer(reducer, initialState);

  const { studentRequests, reviewCount, click, status } = state;

  useEffect(() => {
    localStorage.setItem('studentRequests', JSON.stringify(studentRequests));
  }, [studentRequests]);

  useEffect(() => {
    localStorage.setItem('reviewCount', reviewCount);
  }, [reviewCount]);

  const approved = () => {
    dispatch({ type: 'SET_STATUS', payload: 'Submit' });
  };

  const requiredChanges = () => {
    dispatch({ type: 'SET_STATUS', payload: 'Required Changes' });
    dispatch({ type: 'SET_CLICK', payload: false });
  };

  const request = () => {
    if (reviewCount === 0) {
      dispatch({ type: 'SET_STATUS', payload: 'You have exhausted all your reviews' });
    } else {
      const id = Math.floor(Math.random() * 1000);
      const student = { name: 'Rose', task: 'Mapping', id: id, reviewNum: reviewCount };
      dispatch({ type: 'SET_STUDENT_REQUESTS', payload: [...studentRequests, student] });
      dispatch({ type: 'DECREMENT_REVIEW_COUNT' });
      dispatch({ type: 'SET_CLICK', payload: true });
      dispatch({ type: 'SET_STATUS', payload: 'Pending' });
    }
  };

  return (
    <StudentContext.Provider value={{ state, dispatch }}>
      <div className="App">
        <section className='student'><br/><br/>
          <h1>Student Dashboard</h1>
          <span>{reviewCount}</span><br/>
          <span>{status}</span><br/>
          <span>{!click ? <button onClick={request}>Request</button> : <a href='#link'>View Submission</a>}</span>
        </section>
        <section className='reviewer'><br/><br/>
          <h1>Review Dashboard</h1>
          <span>Reviews: {studentRequests.map((request) => request.name).join(', ')}</span><br/>
          <button onClick={approved}>Approved</button><br/>
          <button onClick={requiredChanges}>Required Changes</button>
        </section>
      </div>
    </StudentContext.Provider>
  );
}

function useStudentContext() {
  const context = useContext(StudentContext);
  if (context === undefined) {
    throw new Error('useStudentContext must be used within a StudentContext.Provider');
  }
  return context;
}

export { useStudentContext };
export default App;
