import { works } from '../pages/studentDashboad.js/Progress_section/data';
export const handleCompleteTask = (
  moduleName,
  blockIndex,
  dayIndex,
  taskIndex
) => {
  const updatedWorks = [...works];
  const module = updatedWorks.find(
    (module) => module.moduleName === moduleName
  );
  if (module) {
    const block = module.blocks[blockIndex];
    if (block) {
      const day = block.days[dayIndex];
      if (day) {
        const task = day.tasks[taskIndex];
        if (task) {
          task.status = 'Completed';
        }
      }
    }
  }
  // Update the state or send the updatedWorks array to the backend
  console.log(updatedWorks);
};
