import React, { useContext, useEffect, useState } from 'react';
import { StoreContext } from '../../Context/store';

const UsersForm = () => {
  const { state, dispatch } = useContext(StoreContext);
  const { db } = state;
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  console.log(username, email, password, role);

  useEffect(() => {
    const openDB = async () => {
      try {
        const db = await openDatabase(); // Your code to open the indexedDB database
        dispatch({ type: 'SET_DB', payload: db });
      } catch (error) {
        dispatch({ type: 'SET_ERROR', payload: error });
      }
    };

    openDB();
  }, []);
  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const transaction = db.transaction(['users'], 'readwrite');
      const store = transaction.objectStore('users');

      const existingUsers = await store.get(username);
      if (existingUsers) {
        setErrorMessage('Username already exist!');
        console.log(errorMessage);
      }
      const newUser = { username, email, password, role };
      store.add(newUser);
      setErrorMessage('Registration successful');
      return errorMessage;
    } catch (error) {
      console.log('Error registering user:', error);
    }
  };

  return (
    <div className="register">
      <section className="display-users">
        <table></table>
      </section>
      <section className="regieter-form">
        <form>
          <input
            type="text"
            className="form-input"
            placeholder="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <br />
          <input
            type="email"
            className="form-input"
            placeholder="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <br />
          <input
            type="password"
            className="form-input"
            placeholder="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <br />
          <select
            className="form-input"
            value={role}
            onChange={(e) => setRole(e.target.value)}
          >
            <option value=""></option>
            <option value="Student">Student</option>
            <option value="CodeReviewer">Code Reviewer</option>
            <option value="Admin">Admin</option>
          </select>
          <br />
          <button
            className="register-btn"
            type="submit"
            onClick={handleRegister}
          >
            Register
          </button>
          <p>{errorMessage}</p>
        </form>
      </section>
    </div>
  );
};

export default UsersForm;
