import { createContext, useEffect, useReducer, useState } from 'react';
import reducer, { initialState } from './reducer';

export const StoreContext = createContext();

export const StoreProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const [progress, setProgress] = useState(0);
  const [total, setTotal] = useState(0);
  const [completedT, setCompletedT] = useState(0);
  useEffect(() => {
    localStorage.setItem('works', JSON.stringify(state.works));
  }, [state.works]);
  // Calculate progress percentage whenever works data changes

  useEffect(() => {
    const updatedWorks = state.works.map((module) => {
      const updatedBlocks = module.blocks.map((block) => {
        const totalTasks = block.days.flatMap((day) => day.tasks).length;
        const completedTasks = block.days
          .flatMap((day) => day.tasks)
          .filter((task) => task.status === 'Completed').length;

        return {
          ...block,
          totalTasks,
          completedTasks,
        };
      });

      return {
        ...module,
        blocks: updatedBlocks,
      };
    });

    const totalTasks = updatedWorks
      .flatMap((module) => module.blocks)
      .reduce((acc, block) => acc + block.totalTasks, 0);

    const completedTasks = updatedWorks
      .flatMap((module) => module.blocks)
      .reduce((acc, block) => acc + block.completedTasks, 0);

    setCompletedT(completedTasks);
    setTotal(totalTasks);
    const percentage = (completedTasks / totalTasks) * 100;
    setProgress(percentage);
  }, [state.works]);

  // change task status
  const updateTaskStatus = (taskIndex, newStatus) => {
    dispatch({
      type: 'UPDATE_TASK_STATUS',
      payload: {
        taskIndex,
        newStatus,
      },
    });
  };

  return (
    <StoreContext.Provider
      value={{ state, dispatch, updateTaskStatus, progress, total, completedT }}
    >
      {children}
    </StoreContext.Provider>
  );
};
