import { StoreProvider } from './Context/store';
import AppContext from './Context/appContext';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import RequestReviewForm from './pages/studentDashboad.js/Progress_section/requestReviewForm';
import Student from './pages/studentDashboad.js/student';
import './sass/index.scss';
import CodeReviewer from './pages/codeReviewer';
import { useEffect, useState } from 'react';

function App() {
  const [selectedTask, setSelectedTask] = useState(null);
  const [completedTasks, setCompletedTasks] = useState([]);
  // Load completed tasks from local storage when the app loads
  useEffect(() => {
    const storedData = localStorage.getItem('completedTasks');
    if (storedData) {
      setCompletedTasks(JSON.parse(storedData));
    }
  }, []);

  // Save completed tasks to local storage when they change
  useEffect(() => {
    localStorage.setItem('completedTasks', JSON.stringify(completedTasks));
  }, [completedTasks]);
  return (
    <div className="App">
      <AppContext.Provider
        value={{
          selectedTask,
          setSelectedTask,
          completedTasks,
          setCompletedTasks,
        }}
      >
        <Router>
          <Routes>
            <Route path="/" element={<Student />} />
            <Route path="/progress-section-link" element={<Student />} />
            <Route path="/attendance-section-link" element={<Student />} />
            <Route path="/codereviewer" element={<CodeReviewer />} />
            <Route path="/requestReviewForm" element={<RequestReviewForm />} />
          </Routes>
        </Router>
      </AppContext.Provider>
    </div>
  );
}

export default App;
