import { useState } from 'react';
import './App.css';

function App() {
  const [studentRequests, setstudentRequests] = useState(JSON.parse(localStorage.getItem('books')) || [])
  let [ reviewCount, setreviewCount] = useState(5);
  const [click, setclick] = useState(false)

  console.log(studentRequests);
  const approved = () =>{
    setstatus('Submit')
  }
  const RequiredChanges = () =>{
    setstatus('Requred Changes')
    setreviewCount(reviewCount);
    setclick(false);
  }

  const [status, setstatus] = useState('Request a review')
 const student = [{name:"Rose", task:"mapping"}]
  const request = () => {
    if(reviewCount === 0){
      setstatus('You have exhusted all you review')
    }
    else{
      setstudentRequests(studentRequests.push(student));
      console.log(studentRequests);
     setreviewCount(reviewCount = reviewCount - 1);
     setclick(true)
      setstatus('pending')

    }
  }


  return (
    <div className="App">
       <section className='student'><br/><br/>
       <h1>Stusent Dashboard</h1>
       <span>{reviewCount}</span><br></br>
        <span>{status}</span><br/>

        <button onClick={request}>{click ? 'View Submission' : 'Request'}</button>
       </section>
       <section className='reviewer'><br/><br/>
       <h1>Review Dashboard</h1>
        <span>Review:{request ? studentRequests
         : ''}</span><br/>
        <button onClick={approved}>Aproved</button><br/>
        <button onClick={RequiredChanges}>RequiredChanges</button>
       </section>

  </div>

  );
}

export default App;
