import './App.css';

function App() {
  const [studentRequests, setstudentRequests] = useState([])
  let reviewCount = 5

  const [reviewerRequest, setreviewerRwquest] = useState([])

  const [status, setstatus] = useState('Request a review')

  const request = () => {
    if(reviewCount <= 0){
      setstatus('You have exhusted all you review')
    }
    else{
      setstudentRequests(studentRequests.push(studentRequests));
      console.log(studentRequests);
      reviewCount =- 1;
      setstatus('pending')

    }
  }


  return (
    <div className="App">
       <section className='student'><br/><br/>
       <h1>Stusent Dashboard</h1>
       <span>{reviewCount}</span><br></br>
        <span>{status}</span><br/>
        <button onClick={request}>Request</button>
       </section>
       <section className='reviewer'><br/><br/>
       <h1>Review Dashboard</h1>
        <span>Review:</span><br/>
        <button>Aproved</button>
       </section>

  </div>

  );
}

export default App;
