import React, { useContext } from 'react';
import { StoreContext } from '../Context/store';
import { Button } from 'react-bootstrap';
import { removeCompletedTask } from '../Context/reducer';
import { type } from '@testing-library/user-event/dist/type';
const CodeReviewer = () => {
  const { state, dispatch } = useContext(StoreContext);
  const { completedTasks } = state;
  const handleRemoveTask = (taskIndex) => {
    dispatch(removeCompletedTask(taskIndex));
    dispatch({ type: 'SUBMIT', payload: 'Submit' });
  };
  return (
    <>
      <table className="table">
        <thead>
          <tr>
            <th>Task Name</th>
            <th>Pull Request Link</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {completedTasks.map((task) => (
            <tr key={task.taskIndex}>
              <td>
                <a href={task.taskLink}>{task.taskName}</a>
              </td>
              <td>
                <a href={task.pullRequestLink}>{task.pullRequestLink}</a>
              </td>
              <td>
                <div className="btn-group">
                  <button
                    className="btn btn-secondary btn-sm dropdown-toggle"
                    type="button"
                    data-bs-toggle="dropdown"
                    aria-expanded="false"
                  >
                    Actions
                  </button>
                  <ul className="dropdown-menu">
                    <li>
                      <Button>Approve</Button>
                    </li>
                    <li>
                      <Button>Require Changes</Button>
                    </li>
                  </ul>
                </div>
              </td>
              <td>
                <Button
                  onClick={() => {
                    handleRemoveTask(task.taskIndex);
                  }}
                >
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default CodeReviewer;
