import React, { useState } from 'react';

function CodeReviewerDashboard() {
  const [reviews, setReviews] = useState([]);

  const handleApproval = (index) => {
    const updatedReviews = [...reviews];
    updatedReviews[index].status = 'approved';
    setReviews(updatedReviews);
  };

  const handleChangesRequired = (index) => {
    const updatedReviews = [...reviews];
    updatedReviews[index].status = 'changes required';
    setReviews(updatedReviews);
  };

  return (
    <div>
      <h2>Code Reviewer Dashboard</h2>
      {reviews.map((review, index) => (
        <div key={index}>
          <p>Student: {review.student}</p>
          <p>Code: {review.code}</p>
          <p>Status: {review.status}</p>
          {review.status === 'pending' && (
            <div>
              <button onClick={() => handleApproval(index)}>Approve</button>
              <button onClick={() => handleChangesRequired(index)}>Changes Required</button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default CodeReviewerDashboard;
