import { useContext } from "react";
import {ReviewContext} from '../Helper/Context'
import {APPROVE_REVIEW, REJECT_REVIEW, } from '../Helper/action'

const CodeReviewerDashboard = () => {
    const { state, dispatch } = useContext(ReviewContext);
  
    const handleApproveReview = (link) => {
      dispatch({
        type: APPROVE_REVIEW,
        payload: {
          link
        }
      });
    };
  
    const handleRejectReview = (link) => {
      dispatch({
        type: REJECT_REVIEW,
        payload: {
          link
        }
      });
    };
  
    return (
      <div>
        <h1>Code Reviewer Dashboard</h1>
        <p>Review Requests:</p>
        <ul>
          {state.codeReviewerRequests.map((request, index) => (
            <li key={index}>
              <a href={request.link} target="_blank" rel="noopener noreferrer">{request.link}</a> - {request.status} 
              <button onClick={() => handleApproveReview(request.link)}>Approve</button>
              <button onClick={() => handleRejectReview(request.link)}>Reject</button>
            </li>
          ))}
        </ul>
      </div>
    );
  };

  export default CodeReviewerDashboard;
